mapper.select:
SELECT COUNT(*)
FROM TBL_BOARD B
WHERE 1=1

    AND KIND = #{kind}



    AND (
        TITLE LIKE '%' || #{keyWord} || '%' OR USERID LIKE '%' || #{keyWord} || '%' 
    )



    AND (
        TITLE LIKE '%' || #{keyWord} || '%' 

    )



    AND (
        USERID LIKE '%' || #{keyWord} || '%' 
    )
